defmodule StairsGainTest do
  use ExUnit.Case, async: true

  @moduletag :stairs_gain

  
  test "it should return 1 in test case 1" do
    assert StairsGain.stairsGain("ygy") == 1
  end

  test "it should return 1 in test case 2" do
    assert StairsGain.stairsGain("gyggygggyygygyyyyyg") == 1
  end

  test "it should return -14 in test case 3" do
    assert StairsGain.stairsGain("gyyyyygggygggggyggggyggygyggg") == -14
  end

  test "it should return -57 in test case 4" do
    assert StairsGain.stairsGain("ygggggggygggggyggggygyggggggggyygyyyygggggggyygyggyyygggggyyyyggggyygggyggggyggggyyyggggyygggygggggg") == -57
  end

  test "it should return 22 in test case 5" do
    assert StairsGain.stairsGain("yyygyygyyggggyyggyyyyggyyggyyyyyygyyyyyyggyyyygggygyyggyyyyyygggygyyyyggygygggyyyyggygyggygygyggygg") == 22
  end

  test "it should return 378 in test case 6" do
    assert StairsGain.stairsGain("") == 378
  end

  test "it should return 94 in test case 7" do
    assert StairsGain.stairsGain("") == 94
  end
end
